SELECT table1.column1, table1.column2, table1.column3, table2.column4, table2.column5, table2.column6 
FROM table1 JOIN table2 ON table1.common_column = table2.common_column 
WHERE table1.unique_column = value1 AND table1.another_column = value2 AND (table2.function_column, table2.another_function_column) = value_tuple;
